RESTBuilder.prototype.raw = function(value) {
	this.$data = value && value.$clean ? value.$clean() : value;
	return this;
};

RESTBuilder.prototype.cookie = function(name, value) {
	if (!this.$cookies)
		this.$cookies = {};
	this.$cookies[name] = value;
	return this;
