/**
 * Streamer method
 * @param {String} beg
 * @param {String} end
 * @param {Function(value, index)} callback
 */
exports.streamer = function(beg, end, callback, skip) {

	if (typeof(end) === 'function') {
		skip = callback;
		callback = end;
		end = undefined;
	}

	var indexer = 0;
	var buffer = exports.createBufferSize();

	if (skip === undefined)
		skip = 0;

	beg = exports.createBuffer(beg, 'utf8');
	if (end)
		end = exports.createBuffer(end, 'utf8');

	if (!end) {
		var length = beg.length;
		return function(chunk) {

			if (!chunk)
				return;

			buffer = Buffer.concat([buffer, chunk]);

			var index = buffer.indexOf(beg);
			if (index === -1)
				return;

			while (index !== -1) {

				if (skip)
					skip--;
				else
					callback(buffer.toString('utf8', 0, index + length), indexer++);

				buffer = buffer.slice(index + length);
				index = buffer.indexOf(beg);
				if (index === -1)
					return;
			}
		};
	}

	var blength = beg.length;
	var elength = end.length;
	var bi = -1;
	var ei = -1;
	var is = false;

	return function(chunk) {

		if (!chunk)
			return;

		buffer = Buffer.concat([buffer, chunk]);

		if (!is) {
			bi = buffer.indexOf(beg);
			if (bi === -1)
				return;
			is = true;
		}

		if (is) {
			ei = buffer.indexOf(end, bi + blength);
			if (ei === -1)
				return;
		}

		while (bi !== -1) {

			if (skip)
				skip--;
			else
				callback(buffer.toString('utf8', bi, ei + elength), indexer++);

			buffer = buffer.slice(ei + elength);
			is = false;
			bi = buffer.indexOf(beg);
			if (bi === -1)
				return;
			is = true;
			ei = buffer.indexOf(end, bi + blength);
			if (ei === -1)
				return;
		}
	};
};

