			return;

		try {
			mailer.emit('error', err, obj);
		} catch(e) {
			F.error(err, 'FrameworkMail');
		}
	});

	socket.on('end', function() {
		mailer.destroy(obj);
		obj.callback && obj.callback();
		obj.callback = null;
	});

	socket.on('data', function(data) {

		if (obj.closed)
			return;

