					var err = new Error('Forbidden.');

					mailer.destroy(obj);
					obj.callback && obj.callback(err);
					obj.callback = null;

					if (obj.try)
						return;

					try {
						mailer.emit('error', err, obj);
					} catch(e) {
						F.error(err, 'FrameworkMail');
					}

					return;
				}

				mailer._writeline(obj, value);
				return;

			case 354:
				mailer._writeline(obj, obj.message);
				mailer._writeattachment(obj);
				obj.message = null;
				return;

			default:

