function DatabaseBuilder() {
	this.$take = 0;
	this.$skip = 0;
	this.$filter = [];
	this.$sort;
	this.$first = false;
	this.$scope = 0;
	this.$fields;
	this.$join;
	this.$joincount;
	this.$callback = NOOP;
	this.$scalar;
	this.$scalarfield;
